const DailyIntentions = () => {
  const [intentions, setIntentions] = useLocalStorage('daily-intentions', ['', '', '']);
  const [completed, setCompleted] = useLocalStorage('daily-completed', [false, false, false]);
  const today = new Date().toLocaleDateString('en-US', { weekday: 'long', month: 'short', day: 'numeric' });

  const updateIntention = (index, value) => {
    const newIntentions = [...intentions];
    newIntentions[index] = value;
    setIntentions(newIntentions);
  };

  const toggleComplete = (index) => {
    const newCompleted = [...completed];
    newCompleted[index] = !newCompleted[index];
    setCompleted(newCompleted);
  };

  const completedCount = completed.filter(Boolean).length;

  return (
    <div className="flex-col">
      <div className="flex" style={{ justifyContent: 'space-between', marginBottom: 16 }}>
        <div>
          <h3 style={{ margin: 0, fontSize: 18 }}>ðŸŽ¯ Today's Intentions</h3>
          <p className="text-sm text-muted">{today}</p>
        </div>
        <div style={{ textAlign: 'right' }}>
          <div style={{ fontSize: 24, fontWeight: 700 }} className={completedCount === 3 ? 'success' : ''}>
            {completedCount}/3
          </div>
          <p className="text-sm text-muted">complete</p>
        </div>
      </div>

      {[0, 1, 2].map((index) => (
        <div key={index} className="card flex" style={{ opacity: completed[index] ? 0.6 : 1 }}>
          <button
            onClick={() => toggleComplete(index)}
            style={{
              width: 28,
              height: 28,
              borderRadius: '50%',
              border: completed[index] ? 'none' : '2px solid #666',
              background: completed[index] ? '#4ade80' : 'transparent',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: 14,
              flexShrink: 0,
            }}
          >
            {completed[index] ? 'âœ“' : ''}
          </button>
          <input
            type="text"
            placeholder={`Intention ${index + 1}...`}
            value={intentions[index]}
            onChange={(e) => updateIntention(index, e.target.value)}
            style={{
              flex: 1,
              textDecoration: completed[index] ? 'line-through' : 'none',
            }}
          />
        </div>
      ))}

      {completedCount === 3 && (
        <div className="card success" style={{ textAlign: 'center', marginTop: 8 }}>
          ðŸŽ‰ All intentions complete! Great work today.
        </div>
      )}
    </div>
  );
};

export default DailyIntentions;
