const SpoonTracker = () => {
  const [spoons, setSpoons] = useLocalStorage('spoon-count', 12);
  const [maxSpoons, setMaxSpoons] = useLocalStorage('max-spoons', 12);
  const [log, setLog] = useLocalStorage('spoon-log', []);

  const useSpoon = (cost = 1) => {
    const newCount = Math.max(0, spoons - cost);
    setSpoons(newCount);
    setLog([...log.slice(-9), { action: 'used', cost, time: new Date().toLocaleTimeString(), remaining: newCount }]);
  };

  const restoreSpoon = () => {
    const newCount = Math.min(maxSpoons, spoons + 1);
    setSpoons(newCount);
    setLog([...log.slice(-9), { action: 'restored', cost: 1, time: new Date().toLocaleTimeString(), remaining: newCount }]);
  };

  const reset = () => {
    setSpoons(maxSpoons);
    setLog([]);
  };

  const percentage = Math.round((spoons / maxSpoons) * 100);
  const status = percentage > 60 ? 'success' : percentage > 30 ? 'warning' : 'error';

  return (
    <div className="flex-col">
      <div style={{ textAlign: 'center', marginBottom: 20 }}>
        <div style={{ fontSize: 48, marginBottom: 8 }}>ü•Ñ</div>
        <div style={{ fontSize: 36, fontWeight: 700 }} className={status}>
          {spoons} / {maxSpoons}
        </div>
        <div className="text-muted text-sm">
          {percentage}% energy remaining
        </div>

        {/* Progress bar */}
        <div style={{
          height: 8,
          backgroundColor: '#333',
          borderRadius: 4,
          marginTop: 12,
          overflow: 'hidden',
        }}>
          <div style={{
            width: `${percentage}%`,
            height: '100%',
            backgroundColor: status === 'success' ? '#4ade80' : status === 'warning' ? '#fb923c' : '#ef4444',
            transition: 'width 0.3s ease',
          }} />
        </div>
      </div>

      <div className="flex" style={{ justifyContent: 'center', gap: 12, marginBottom: 16 }}>
        <button onClick={() => useSpoon(1)} disabled={spoons === 0}>
          -1 Spoon
        </button>
        <button onClick={() => useSpoon(2)} disabled={spoons < 2}>
          -2 Spoons
        </button>
        <button onClick={restoreSpoon} disabled={spoons >= maxSpoons} style={{ background: '#4ade80' }}>
          +1 Rest
        </button>
      </div>

      <button onClick={reset} style={{ background: '#666', width: '100%' }}>
        Reset Day
      </button>

      {spoons <= 3 && spoons > 0 && (
        <div className="card warning" style={{ marginTop: 12, textAlign: 'center' }}>
          ‚ö†Ô∏è Low energy. Consider resting.
        </div>
      )}

      {spoons === 0 && (
        <div className="card error" style={{ marginTop: 12, textAlign: 'center' }}>
          üõë Empty. Rest is mandatory. You've got nothing left to give.
        </div>
      )}
    </div>
  );
};

export default SpoonTracker;
